package com.example.hridoy;

public class Page186_5_24_a {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int balance = 10;
		while(true) {
			if(balance < 9)
				break;
			balance = balance - 9;
		}
		
		System.out.println("Balance is " + balance);

	}

}
